INSERT INTO `mrc_posts` VALUES ('137', '2', '2016-07-31 13:36:28', '2016-07-31 13:36:28', '<h3>We have sixteen beautifully crafted Pandaw ships in operation that we built ourselves – plus our first coastal ship the Andaman Explorer. Each ship, hand-finished in brass and teak by traditional craftsmen, is itself an object of great beauty.</h3>\r\nThe secret of our success is that, whilst luxury and comfort are ever-present, on our ships it is the colonial-era character and friendly atmosphere that predominate. All our Pandaws have ultra-shallow drafts and can travel to remote areas, which are unreachable by other passenger-carrying vessels, let alone overland.', 'Ships', '', 'publish', 'closed', 'closed', '', 'ships', '', '', '2016-08-17 08:36:54', '2016-08-17 08:36:54', '', '0', 'http://local.mrc.com/?page_id=137', '0', 'page', '', '0');
