INSERT INTO `mrc_posts` VALUES ('215', '4', '2016-08-17 08:36:54', '2016-08-17 08:36:54', '<h3>We have sixteen beautifully crafted Pandaw ships in operation that we built ourselves – plus our first coastal ship the Andaman Explorer. Each ship, hand-finished in brass and teak by traditional craftsmen, is itself an object of great beauty.</h3>\r\nThe secret of our success is that, whilst luxury and comfort are ever-present, on our ships it is the colonial-era character and friendly atmosphere that predominate. All our Pandaws have ultra-shallow drafts and can travel to remote areas, which are unreachable by other passenger-carrying vessels, let alone overland.', 'Ships', '', 'inherit', 'closed', 'closed', '', '137-revision-v1', '', '', '2016-08-17 08:36:54', '2016-08-17 08:36:54', '', '137', 'http://www.vietcruises.com/uncategorized/137-revision-v1-215.html', '0', 'revision', '', '0');
