INSERT INTO `mrc_options` VALUES ('14773', 'home_introduction', '<h2 class=\"homepageh2\" style=\"text-align: center;\"><span style=\"color: #1b75bc;\"><strong>DISCOVER MEKONG DELTA\'S <span class=\"highlighth2\">FASCINATING RIVERS</span></strong></span></h2>\r\n<p style=\"text-align: justify;\"><em>A journey with Viet Princess Cruises along the legendary rivers of Mekong Delta is an unforgettable fusion of distinctive sights, colorful cultures, and ancient traditions. Inspired by cruisers, our ships and itineraries stand out from the crowd. And because bigger isn\'t always better, we focus on intimate experiences with exceptional personalized service on our new Suite Ships® in Southeast Asia.</em></p>', 'yes');
INSERT INTO `mrc_options` VALUES ('14774', 'offer_introduction', '<p class=\"homepageh2\" style=\"text-align: left;\"><em>A journey with Viet Princess Cruises along the legendary rivers of Mekong Delta is an unforgettable fusion of distinctive sights, colorful cultures, and ancient traditions. Inspired by cruisers, our ships and itineraries stand out from the crowd. And because bigger isn\'t always better, we focus on intimate experiences with exceptional personalized service on our new Suite Ships® in Southeast Asia.</em></p>', 'yes');
INSERT INTO `mrc_options` VALUES ('14775', 'ship_introduction', '<h3>We have three beautifully crafted Viet Princess Cruises ships in operation that we built ourselves. Each ship, hand-finished in brass and teak by traditional craftsmen, is itself an object of great beauty.</h3>\r\nThe secret of our success is that, whilst luxury and comfort are ever-present, on our ships it is the colonial-era character and friendly atmosphere that predominate. All our Viet Princess Cruises have ultra-shallow drafts and can travel to remote areas, which are unreachable by other passenger-carrying vessels, let alone overland.', 'yes');
