INSERT INTO `mrc_postmeta` VALUES ('1589', '144', 'title1', 'INNOVATIONS');
INSERT INTO `mrc_postmeta` VALUES ('1590', '144', '_title1', 'field_5801a997bd27a');
INSERT INTO `mrc_postmeta` VALUES ('1591', '144', 'content1', 'With twelve luxurious cabins or suites, we are small, intimate boutique in style, and highly personable. At no time should our guests feel part of a ‘tour group’. We like to welcome and entertain travellers, people who have seen the World and expect a five-star travel experience.\r\n\r\nWe are the only cruise operation that commences each upstream journey from Saigon Port, located in the center of Saigon city, on the Saigon River. Access to the vessel from the CBD hotels is less than ten minutes by car.\r\n\r\n');
INSERT INTO `mrc_postmeta` VALUES ('1592', '144', '_content1', 'field_5801a9a1bd27b');
