INSERT INTO `mrc_postmeta` VALUES ('2269', '715', 'introduction_content', '<h4 class=\"homepageh2\">DISCOVER MEKONG DELTA\'S <span class=\"highlighth2\">FASCINATING RIVERS</span></h4>\r\n<h4 style=\"text-align: justify;\"><em>A journey with Viet Princess Cruises along the legendary rivers of Mekong Delta is an unforgettable fusion of distinctive sights, colorful cultures, and ancient traditions. Inspired by cruisers, our ships and itineraries stand out from the crowd. And because bigger isn\'t always better, we focus on intimate experiences with exceptional personalized service on our new Suite Ships® in Southeast Asia.</em></h4>');
